from django.db.models.sql.where import WhereNode, XOR
from django.db.models.lookups import Exact
from django.db.models.expressions import CombinedExpression, Combinable

class Dummy:
    def __init__(self, name):
        self.name = name
    def __repr__(self):
        return f"Dummy({self.name})"

class FakeConnection:
    class features:
        supports_logical_xor = False

class FakeCompiler:
    def __init__(self):
        self.query = None
    def compile(self, obj):
        # Inspect Exact nodes to see rhs
        if isinstance(obj, Exact):
            rhs = obj.rhs
            is_mod = isinstance(rhs, CombinedExpression) and getattr(rhs, 'connector', None) == Combinable.MOD
            print('Exact rhs is CombinedExpression with MOD?', is_mod)
            if is_mod:
                print('PASS')
                return ('', [])
            else:
                raise SystemExit('FAIL: rhs not modulo expression')
        # fallback
        if hasattr(obj, 'as_sql'):
            return obj.as_sql(self, FakeConnection())
        return (str(obj), [])

# Build WhereNode with XOR and 3 dummy children
children = [Dummy(1), Dummy(1), Dummy(1)]
wn = WhereNode(children, XOR)
try:
    wn.as_sql(FakeCompiler(), FakeConnection())
except SystemExit as e:
    print(e)
    raise
'''
open('reproduce_issue.py','w').write(content)
print('wrote reproduce_issue.py')
