# reproduce_issue.py
# Demonstrates the EncodedFile behavior:
# - OldEncodedFile (no mode property) would advertise the underlying buffer's 'mode' (including 'b')
#   causing callers to write bytes, but its write() expects text, raising TypeError.
# - NewEncodedFile hides the 'b' flag in mode so callers write text and no error occurs.

class FakeBuffer:
    def __init__(self, mode):
        self.mode = mode
        self.written = []
    def write(self, obj):
        # This fake buffer only accepts str, raises if bytes provided
        if isinstance(obj, str):
            raise TypeError("underlying buffer expects bytes, not str")
        self.written.append(obj)
    def __repr__(self):
        return '<FakeBuffer mode=%r>' % (self.mode,)

import six

# Old behavior: no mode property override
class OldEncodedFile(object):
    def __init__(self, buffer, encoding):
        self.buffer = buffer
        self.encoding = encoding
    def write(self, obj):
        if isinstance(obj, six.text_type):
            obj = obj.encode(self.encoding, "replace")
        else:
            # in PY3 would raise, but simulate accepting bytes path
            pass
        self.buffer.write(obj)
    def writelines(self, linelist):
        data = "".join(linelist)
        self.write(data)
    @property
    def name(self):
        return repr(self.buffer)
    def __getattr__(self, name):
        return getattr(self.buffer, name)

# New behavior: mode property hides 'b'
class NewEncodedFile(object):
    def __init__(self, buffer, encoding):
        self.buffer = buffer
        self.encoding = encoding
    def write(self, obj):
        if isinstance(obj, six.text_type):
            obj = obj.encode(self.encoding, "replace")
        else:
            pass
        self.buffer.write(obj)
    def writelines(self, linelist):
        data = "".join(linelist)
        self.write(data)
    @property
    def name(self):
        return repr(self.buffer)
    @property
    def mode(self):
        m = getattr(self.buffer, "mode", "")
        return m.replace('b','')
    def __getattr__(self, name):
        return getattr(self.buffer, name)

# Simulate youtube-dl's write behavior: if 'b' in out.mode -> write bytes, else write str

def simulated_youtubedl_write(out, text):
    if 'b' in getattr(out, 'mode', ''):
        # writes bytes
        out.write(text.encode('utf8'))
    else:
        out.write(text)


def run_test(encoded_cls):
    buf = FakeBuffer('rb+')
    enc = encoded_cls(buf, 'utf8')
    try:
        simulated_youtubedl_write(enc, 'hello')
    except Exception as e:
        return False, e
    return True, buf.written

if __name__ == '__main__':
    ok_old, res_old = run_test(OldEncodedFile)
    print('OldDecoded success:', ok_old, 'result:', res_old)
    ok_new, res_new = run_test(NewEncodedFile)
    print('NewDecoded success:', ok_new, 'result:', res_new)
    if ok_old:
        raise SystemExit('Expected old behavior to fail but it succeeded')
    if not ok_new:
        raise SystemExit('Expected new behavior to succeed but it failed: %r' % (res_new,))
    print('Reproduction: old fails, new passes')
